PageResourcePortable.Content:
var pagina = Sys.WebForms.PageRequestManager.getInstance();
if (!pagina.get_isInAsyncPostBack()) {
	pagina.add_pageLoaded(function(){
        $('#ctl00_Main_CARGADEINFORMAESDASMOVIMENTAESCONTBEIS_PageControl_GERAL_GERAL_TABELASMOVIMENTACAO').parent().find('.row').children().each(function (index) {
            $(this).attr('class', 'col-lg-12 col-md-12 col-sm-12');
            $(this).children().css('margin-bottom', '0px');
            $(this).children().css('margin-top', '0px');
            $(this).children().find('label').css('margin-top', '0px');
            $(this).children().find('label').css('margin-bottom', '1px');
        });	    

        $('#ctl00_Main_CARGADEINFORMAESDASMOVIMENTAESCONTBEIS_PageControl_GERAL_GERAL_FILTRO').parent().find('.row').children().each(function (index) {
            if (index == 1 || index == 2) {
                $(this).attr('class', 'col-lg-1 col-md-3 col-sm-3');
            }
        });        
	});
}
