PageResourcePortable.Content:
function BaixarDANFE(handleDocumento) {
    var params = {
        handleDocumento
    }

    $.post(Benner.Page.getApplicationPath() + 'api/PortalCliente/BaixarDANFE', params)
        .done(function () {
            document.location.reload();
        });

}

