def OnCMD_VERIFICARQUANTIDADEDEPLANOSExecute(command, entity):
    from Benner.Tecnologia.Common.Components import *
    from Benner.Tecnologia.Common import EntityDefinition
    from Benner.Tecnologia.Wes.Components import CommandsUtility
    entityDefinition = EntityDefinition.GetByName("CT_CONTAS");
    funcaoQuantidade = function.Call(entityDefinition, "VerificarQuantidadeDePlanos");
    if (funcaoQuantidade.Vars["EHPLANOUNICO"] == "S"):
        funcaoPlano = function.Call(entityDefinition, "TrazUnicoPlano");
        command.OutputDefinition = FormLinkDefinition()
        command.OutputDefinition.TargetSystemInstanceName = "CORPORATIVO"
        command.OutputDefinition.TargetEntityDefinitionName = "CT_PLANOVERSOES";
        command.OutputDefinition.TargetEntityHandle = funcaoPlano.Vars["PLANO"];
        command.OutputDefinition.Url = "~/aga/a/f/aga_t3305_at5359_hsfec1cbd6.aspx";
