ScriptItemPortable.SourceCode:
def Load(widget):
    if (widget.ProviderWidget.Entity != None):
        providerEntity = widget.ProviderWidget.Entity;
        if ((providerEntity.Definition.Name == "AT_INVENTARIOS") and (providerEntity.Fields.ContainsKey("STATUS"))):
            widget.Hidden = providerEntity["STATUS"].Value.Value != 1 and providerEntity["STATUS"].Value.Value !=3; # cadastrado e com erro 

    import clr
    clr.AddReference("Benner.Erp.Web")
    from Benner.Erp.Web import AuthorizationHelper

    widget.View.Commands["DELETE"].Visible = AuthorizationHelper.HasTask("EXCLUIR_CONSULTARINVENTARIO")
    widget.View.Commands["EDIT"].Visible = AuthorizationHelper.HasTask("MANTER_CONSULTARINVENTARIO")
    widget.View.Commands["New"].Visible = AuthorizationHelper.HasTask("MANTER_CONSULTARINVENTARIO")
