ScriptItemPortable.SourceCode:
def Load(widget):
    if (widget.ProviderWidget != None):
        if (widget.ProviderWidget.Entity.Fields.ContainsKey("ULTIMONIVEL")):
            widget.Hidden = not widget.ProviderWidget.Entity["ULTIMONIVEL"].Value;

    import clr
    clr.AddReference("Benner.Erp.Web")
    from Benner.Erp.Web import AuthorizationHelper

    widget.View.Commands["DELETE"].Visible = AuthorizationHelper.HasTask("EXCLUIR_ESPECIESBENS") or AuthorizationHelper.HasTask("EXCLUIR_LOCALIZACOES") or AuthorizationHelper.HasTask("EXCLUIR_BENSDOIMOBILIZADO")

    widget.View.Commands["EDIT"].Visible = AuthorizationHelper.HasTask("MANTER_ESPECIESBENS") or AuthorizationHelper.HasTask("MANTER_LOCALIZACOES") or AuthorizationHelper.HasTask("MANTER_BENSDOIMOBILIZADO")

    widget.View.Commands["New"].Visible = AuthorizationHelper.HasTask("MANTER_ESPECIESBENS") or AuthorizationHelper.HasTask("MANTER_LOCALIZACOES") or AuthorizationHelper.HasTask("MANTER_BENSDOIMOBILIZADO")
