ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    contratoAprovado = entity.ContratoAprovado(long(entity.Fields["CONTRATO"].Handle.Value))

    if (contratoAprovado):
        statusAditivo = int(entity.Fields["STATUS"].Value)
        statusCadastrado = 1    
        if (statusAditivo > statusCadastrado):
            if row.EditCommand != None: row.EditCommand.Visible = False
            if row.DeleteCommand != None: row.DeleteCommand.Visible = False

    if row.GetCustomCommand("AcompanharAssinaturasDigitais") != None:    
        row.GetCustomCommand("AcompanharAssinaturasDigitais").Enabled = entity.ExibirComandoAcompanharAssinaturasDigitais

    if row.GetCustomCommand("VoltarParaElaboracao") != None:    
        row.GetCustomCommand("VoltarParaElaboracao").Enabled = entity.PodeVoltarParaElaboracao
