ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")
    from Benner.Corporativo.Definicoes.Contratos import CNContratoGlosaMulta

    handle = entity.Handle

    if row.EditCommand != None: row.EditCommand.Visible = CNContratoGlosaMulta.PodeEditarExcluirGlosaMulta(handle)
    if row.DeleteCommand != None: row.DeleteCommand.Visible = CNContratoGlosaMulta.PodeEditarExcluirGlosaMulta(handle)
    if row.GetCustomCommand("CMD_CONFIRMAR") != None: row.GetCustomCommand("CMD_CONFIRMAR").Enabled = CNContratoGlosaMulta.PodeConfirmarGlosaMulta(handle)
    if row.GetCustomCommand("CMD_CANCELAR") != None: row.GetCustomCommand("CMD_CANCELAR").Enabled = CNContratoGlosaMulta.PodeCancelarGlosaMulta(handle)
    if row.GetCustomCommand("CMD_LANCFINANCEIRO") != None: row.GetCustomCommand("CMD_LANCFINANCEIRO").Enabled = CNContratoGlosaMulta.PodeGerarLancamentosFinanceiros(handle)
