ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")
    from Benner.Corporativo.Definicoes.Contratos import CNContratoObjeto

    if (entity.Handle != None):
        contratoEmElaboracao = CNContratoObjeto.ContratoEmElaboracao(entity.ContratoHandle)
        if row.GetCustomCommand("btnGerarPrevisao") <> None: row.GetCustomCommand("btnGerarPrevisao").Enabled = contratoEmElaboracao
        if row.GetCustomCommand("btnExcluirTodas") <> None: row.GetCustomCommand("btnExcluirTodas").Enabled = contratoEmElaboracao
        if row.EditCommand <> None: row.EditCommand.Visible = contratoEmElaboracao
        if row.DeleteCommand <> None: row.DeleteCommand.Visible = contratoEmElaboracao

    if entity.Fields["TEMAPONTAMENTO"] == True:
    	row.GetCellByName("TEMAPONTAMENTOSTRING").Text = "Sim"
    if entity.Fields["APONTAMENTOCOMPLETO"] == True:
    	row.GetCellByName("APONTAMENTOCOMPLETOSTRING").Text = "Sim"
    if entity.Fields["INCLUIRCONTRATO"] == True:
    	row.GetCellByName("INCLUIRCONTRATOSTRING").Text = "Sim"
    if entity.Fields["ITEMPROPRIEDADE"] == True:
    	row.GetCellByName("ITEMPROPRIEDADESTRING").Text = "Sim"
    if entity.Fields["IGNORARRATEIODESCONTO"] == True:
    	row.GetCellByName("IGNORARRATEIODESCONTOSTRING").Text = "Sim"
    if entity.Fields["FATURARINDIVIDUALMENTE"] == True:
    	row.GetCellByName("FATURARINDIVIDUALMENTESTRING").Text = "Sim"
