ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")
    from Benner.Corporativo.Definicoes.Contratos import CNContrato

    statusContrato = entity.Fields["STATUS"].Value
    liberado = entity.Fields["LIBERADO"]
    statusEmElaboracao = 1    
    if (((statusContrato == statusEmElaboracao) and liberado) or (statusContrato > statusEmElaboracao)): 
        if row.EditCommand != None:
            row.EditCommand.Visible = False
        if row.DeleteCommand != None:    
            row.DeleteCommand.Visible = False
    if ((statusContrato == statusEmElaboracao) and liberado): 
        row.GetCellByName("STATUS").Text = "Aguardando confirmação"

    if row.GetCustomCommand("AcompanharAssinaturasDigitais") != None:    
        row.GetCustomCommand("AcompanharAssinaturasDigitais").Enabled = entity.ExibirComandoAcompanharAssinaturasDigitais

    if row.GetCustomCommand("VoltarParaElaboracao") != None:    
        row.GetCustomCommand("VoltarParaElaboracao").Enabled = entity.PodeVoltarParaElaboracao

    if CNContrato.VerificarContradoVencido(entity.Fields["HANDLE"].Value):    
        row.GetCellByName("STATUS").Text = "Vencido"
