ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if entity == None: return

    status = entity.Fields["STATUS"].Value
    liberado = entity.Fields["LIBERADO"]
    statusEmElaboracao = 1
    if (((status == statusEmElaboracao) and liberado) or (status > statusEmElaboracao)):
        if row.EditCommand != None:
            row.EditCommand.Visible = False
        if row.DeleteCommand != None:    
            row.DeleteCommand.Visible = False
    if ((status == statusEmElaboracao) and liberado):
        row.GetCellByName("STATUS").Text = "Aguardando confirmação"

    if row.GetCustomCommand("CMD_CONTRATOCONVENIOVOLTARPARAELABORACAO") != None:
        row.GetCustomCommand("CMD_CONTRATOCONVENIOVOLTARPARAELABORACAO").Enabled = entity.PodeVoltarParaElaboracao
