def OnEmitirExecute(command, entity):
    from Benner.Tecnologia.Common import BreadcrumbEntityHandle, WesSiteMapNode
    breadCrumbList = WesSiteMapNode.GetBreadcrumbEntityHandles()

    item = breadCrumbList.Find(lambda found:found.EntityDefinitionName.Equals("CN_CONTRATOADITIVOS"))

    if item.Handle == None :
        item = breadCrumbList.Find(lambda found:found.EntityDefinitionName.Equals("CN_CONTRATOS"))   
        command.TransitoryVars.Add("EHADITIVO", False)
    else : 
        command.TransitoryVars.Add("EHADITIVO", True)        

    command.TransitoryVars.Add("HANDLE", item.Handle)
