ScriptItemPortable.SourceCode:
def Load(widget):
    form = widget.ProviderWidget
    if (form == None) : return

    entity = form.Entity
    if (entity == None) :
        widget.Hidden = True
        return

    if (entity["SQL_CONTASCALCULADAS"].GetDBValue() == None) or (entity["ULTIMONIVEL"].GetDBValue() == "N"):
        widget.Hidden = True
