def OnDLPAValueChanged(view, entity):
    if (entity.Fields["DLPA"].Handle != None and entity.Fields["DLPA"].Handle.IsValid()):
		entity.Fields["DMPL"].Handle = None

def OnDMPLValueChanged(view, entity):
    if (entity.Fields["DMPL"].Handle != None and entity.Fields["DMPL"].Handle.IsValid()):
		entity.Fields["DLPA"].Handle = None
