def OnDeleteExecute(command, entity):

    if(entity.Fields["TIPOESCRITURACAO"].Value == 2):
        command.RequestConfirmationBeforeExecute("A exclusão deste Período resultará na exclusão dos seus respectivos históricos e Livros Auxiliares. Deseja Continuar?")
    else:
        command.RequestConfirmationBeforeExecute("A exclusão deste Período resultará na exclusão dos seus respectivos históricos. Deseja Continuar?")
