def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.ERP.Comum")
    from Benner.ERP.Comum import Utils
    row.EditCommand.Visible = Utils.VerificaPermissao("MANTERFORNECEDOR");
    row.DeleteCommand.Visible = Utils.VerificaPermissao("EXCLUIRFORNECEDOR");
