ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if entity["ACOPLADO"].GetBoolean():
        row.GetCustomCommand("CMD_ACOPLARGRID").Enabled = False
    elif entity["DESACOPLADO"].GetBoolean():
        row.GetCustomCommand("CMD_ACOPLARGRID").Enabled = False
        row.GetCustomCommand("CMD_DESACOPLARGRID").Enabled = False
    else:
        row.GetCustomCommand("CMD_DESACOPLARGRID").Enabled = False
        row.GetCustomCommand("CMD_VOLTARGRID").Enabled = False
