ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Tecnologia.Common import EntityState
    import clr

    if widget.Entity == None: return

    entity = widget.Entity

    if widget.View.FieldDefinitions["VALORMAXIMONEGOCIAVEL"] != None:
        widget.View.FieldDefinitions["VALORMAXIMONEGOCIAVEL"].Visible = entity.Fields["PERMITESOLREGULARIZACAONEGOCIA"]          

    if (widget.Entity.State == EntityState.Read):
        if widget.View.FieldDefinitions["VALORMAXIMOPEQUENASCOMPRAS"] != None:
            widget.View.FieldDefinitions["VALORMAXIMOPEQUENASCOMPRAS"].Visible = entity.Fields["PERMITESOLREGULARIZACAOPEQCOMP"]

        if widget.View.FieldDefinitions["EXCEDENTEPEQUENASCOMPRAS"] != None:
            widget.View.FieldDefinitions["EXCEDENTEPEQUENASCOMPRAS"].Visible = entity.Fields["PERMITESOLREGULARIZACAOPEQCOMP"] and entity.Fields["TIPOALCADASOLICITACAO"].Value <> "N" and entity.Fields["TIPOALCADASOLICITACAO"].Value <> "W"

        if widget.View.FieldDefinitions["EXCEDENTENEGOCIAVEL"] != None:
            widget.View.FieldDefinitions["EXCEDENTENEGOCIAVEL"].Visible = entity.Fields["PERMITESOLREGULARIZACAONEGOCIA"] and entity.Fields["TIPOALCADASOLICITACAO"].Value <> "N" and entity.Fields["TIPOALCADASOLICITACAO"].Value <> "W"
