def Load(widget): import clr clr.AddReference("Benner.Corporativo.Definicoes")
if widget.ProviderWidget == None: return
from Benner.Corporativo.Definicoes.Materiais import PDAlmoxarifadoLocalizacao entidade = widget.ProviderWidget.Entity.Definition.Name
if entidade == PDAlmoxarifadoLocalizacao.GetEntityDefinition().Name: widget.Hidden = PDAlmoxarifadoLocalizacao.LocalizacaoProdutosMostrarTab(widget.Breadcrumb.Parent.Entity.Handle)

def InitializeRow(entity, row):
    if row.GetCustomCommand("CMD_GERARCLIENTES") <> None: row.GetCustomCommand("CMD_GERARCLIENTES").Enabled = not entity.TemVariacao
    if row.GetCustomCommand("CMD_GERARFORNECEDORES") <> None: row.GetCustomCommand("CMD_GERARFORNECEDORES").Enabled = not entity.TemVariacao

from Benner.Tecnologia.Common.Exceptions import CancelledOperationException
def OnCMD_GERARCLIENTESExecute(command, entity):
    if identity.Branch.ToString() == "0":
        raise CancelledOperationException("Você precisa selecionar uma filial.")

from Benner.Tecnologia.Common.Exceptions import CancelledOperationException
