def InitializeRow(entity, row):
    HandleOP = entity.Fields["APONTAMENTO"].Instance.Fields["ORDEMDESMONTAGEM"].Instance.Fields["ORDEMPRODUCAO"].Instance
    StatusAP = entity.Fields["APONTAMENTO"].Instance.Fields["STATUS"].Value

    if row.EditCommand != None:
	    row.EditCommand.Visible = False
    if row.DeleteCommand != None:
	    row.DeleteCommand.Visible = False

    if(HandleOP == None and StatusAP == 1):
        if row.EditCommand != None:
            row.EditCommand.Visible = True
        if row.DeleteCommand != None:
            row.DeleteCommand.Visible = True

def Load(widget):
    mestre = widget.ProviderWidget
    entidadeMestre = mestre.Entity
    if widget.View.Commands["Edit"] != None:
        widget.View.Commands["Edit"].Visible = False
    if widget.View.Commands["Delete"] != None:
        widget.View.Commands["Delete"].Visible = False
    if widget.View.Commands["New"] != None:
        widget.View.Commands["New"].Visible = False
    if(entidadeMestre.Fields["STATUS"].Value == 1):
        widget.View.Commands["New"].Visible = True
        widget.View.Commands["Delete"].Visible = True
        widget.View.Commands["Edit"].Visible = True
