def Load(widget):
    form = widget.ProviderWidget
    entity = form.Entity

    if (entity == None) : return

    if ( (str(widget.ID) == "WIDGET_1127") and  ( entity["EHRETENCAO"].GetDBValue() == "S"  )):
        widget.Hidden = False
    else:
        widget.Hidden = True
