def Load(widget):
    form = widget.ProviderWidget
    entity = form.Entity

    if (entity == None) : return

    if ((str(widget.ID) == "WIDGET_311_8057") and entity["ICMSDIFAFCP"].GetDBValue() == "N"):
        widget.Hidden = True

def OnDeleteExecute(command, entity):
    if entity == None: return

    if entity.ExistemRegistrosFilhosFormulas():
        command.RequestConfirmationBeforeExecute("Existem fórmulas vinculadas a esta configuração que serão excluídas. Deseja continuar?")
