ScriptItemPortable.SourceCode:
def Load(widget):
    registro = widget.Entity;
    if registro == None: return;

    calculoPossuiFormulas = registro.CalculoPossuiFormulas();
    widget.View.Commands["CMD_INICIALIZARFORMULAS"].Visible = not calculoPossuiFormulas;
    widget.View.Commands["CMD_REINICIALIZARFORMULAS"].Visible = calculoPossuiFormulas;
    widget.View.Commands["CMD_APAGARFORMULAS"].Visible = calculoPossuiFormulas;
