def Load(widget):
    if (widget.ProviderWidget != None):
        providerEntity = widget.ProviderWidget.Entity;
        if ((providerEntity.Definition.Name == "AT_INVENTARIOS") and (providerEntity.Fields.ContainsKey("STATUS"))):
            widget.Hidden = providerEntity["STATUS"].Value.Value == 1 or providerEntity["STATUS"].Value.Value == 3; # cadastrado ou com erro
