def OnDESCONTOSValueChanged(view, entity):

    valor = entity["VALORPARCELA"].GetDecimal() + entity["JUROS"].GetDecimal() + entity["MULTA"].GetDecimal() - entity["DESCONTOS"].GetDecimal()
    entity["VALORTOTAL"].Value = valor

