ScriptItemPortable.SourceCode:
def OnTIPOEMISSAOValueChanged(view, entity):

    tipo = entity.Fields["TIPOEMISSAO"]
    if tipo == None: return

    valor = tipo.Value
    if valor == 2:
        entity.Fields["ASSUNTO"] = "Boleto da parcela " + entity.Fields["PARCELA"].ToString() + " emitido";
        entity.Fields["CORPO"] = "Boleto com vencimento em " + entity.Fields["PARCELA"].Instance.Fields["DATAVENCIMENTO"].ToString("dd/MM/yyyy");
