ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    from System import String
    from Benner.Tecnologia.Common import ListItem

    if entity == None: return
    if entity["STATUS"].IsNull: return

    status = entity.Fields["STATUS"]
    if status.Value in [1]:
        row.GetCellByName("STATUS").Text = String.Format("<span class=\"label label-default\"> {0} </span>", entity["STATUS"].GetString())
    elif status.Value in [2,3]:
        row.GetCellByName("STATUS").Text = String.Format("<span class=\"label label-warning\"> {0} </span>", entity["STATUS"].GetString())
    elif status.Value in [4]:
        row.GetCellByName("STATUS").Text = String.Format("<span class=\"label label-success\"> {0} </span>", entity["STATUS"].GetString())
    elif status.Value in [5,6]:
        row.GetCellByName("STATUS").Text = String.Format("<span class=\"label label-danger\"> {0} </span>", entity["STATUS"].GetString())

    row.GetCustomCommand("CMD_PROCESSAR_COBRANCA").Enabled = (status.Value in [1,5,6]);
