ScriptItemPortable.SourceCode:
def OnRECEBIDOValueChanged(view, entity):

    valorParcela = 0
    if (entity["PARCELAS"].GetInt32() > 0):
        valorParcela = entity["VALOR"].GetDecimal() / entity["PARCELAS"].GetInt32()
    else:
        valorParcela = entity["VALOR"].GetDecimal() 
    valor = valorParcela + entity["JUROS"].GetDecimal() + entity["MULTA"].GetDecimal() - entity["DESCONTOS"].GetDecimal()
    entity["VALORTOTAL"].Value = valor

