ScriptItemPortable.SourceCode:
def OnEHPESSOAFISICAValueChanged(view, entity):
    if True == (entity.Fields["EHPESSOAFISICA"]) : 
        view.FieldDefinitions["CPFCNPJ"].Title = "CPF";
        view.FieldDefinitions["TELEFONE"].Visible = True;
        view.FieldDefinitions["RG"].Visible = True;
        view.FieldDefinitions["EMAIL"].Visible = True;
        view.FieldDefinitions["ENDERECO"].Visible = True;
        view.FieldDefinitions["NUMERO"].Visible = True;
        view.FieldDefinitions["COMPLEMENTO"].Visible = True;
        view.FieldDefinitions["BAIRRO"].Visible = True;
        view.FieldDefinitions["CIDADE"].Visible = True;
    if False == (entity.Fields["EHPESSOAFISICA"]) : 
        view.FieldDefinitions["CPFCNPJ"].Title = "CNPJ";
        view.FieldDefinitions["TELEFONE"].Visible = False;
        view.FieldDefinitions["RG"].Visible = False;
        view.FieldDefinitions["EMAIL"].Visible = False;
        view.FieldDefinitions["ENDERECO"].Visible = False;
        view.FieldDefinitions["NUMERO"].Visible = False;
        view.FieldDefinitions["COMPLEMENTO"].Visible = False;
        view.FieldDefinitions["BAIRRO"].Visible = False;
        view.FieldDefinitions["CIDADE"].Visible = False;
