def Load(widget):
    entity = widget.Entity
    if entity == None:
        return

    widget.View.Commands["New"].Visible = False

def OnCMD_VoltarExecute(command, entity):
    from Benner.Tecnologia.Common.Components import UrlLinkDefinition

    command.OutputDefinition = UrlLinkDefinition("~/Erp/a/vendas/nfs/Grid.aspx");
