def InitializeRow(entity, row):
    if row.EditCommand != None: 
        row.EditCommand.Visible = entity.Fields["STATUSPROPOSTA"].Value == 1 and  entity.Fields["LIBERADOCONFIRMACAO"] == False
    if row.DeleteCommand != None:
        row.DeleteCommand.Visible = entity.Fields["STATUSPROPOSTA"].Value == 1 and  entity.Fields["LIBERADOCONFIRMACAO"] == False
