def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.ERP.Comum")
    from Benner.ERP.Comum import Utils

    if row.DeleteCommand != None:
        row.DeleteCommand.Visible = Utils.VerificaPermissao("EXCLUIR_FATURAMENTO");
    if row.EditCommand != None:
        row.EditCommand.Visible = Utils.VerificaPermissao("MANTER_FATURAMENTO");

def Load(widget):
    import clr
    clr.AddReference("Benner.ERP.Comum")
    from Benner.ERP.Comum import Utils

    if widget.View.Commands["New"] != None:
        widget.View.Commands["New"].Visible = Utils.VerificaPermissao("MANTER_FATURAMENTO")
