def Load(widget): mestre = widget.ProviderWidget entidade = mestre.Entity status = entidade["STATUS"].GetInt32()
widget.View.Commands["New"].Visible = False widget.View.Commands["Edit"].Visible = False widget.View.Commands["Delete"].Visible = False
if status == 1: widget.View.Commands["New"].Visible = True widget.View.Commands["Edit"].Visible = True widget.View.Commands["Delete"].Visible = True
