def Load(widget):
    from Benner.Tecnologia.Common import EntityDefinition
    from Benner.Tecnologia.Wes.Components import CommandsUtility
    from Benner.Tecnologia.Common import EntityState

    entity = widget.Entity
    if entity == None: return

    if (entity.State == EntityState.Initialized) : return

    entityDefinition = EntityDefinition.GetByName("CM_ORDENSVENDA");
    funcao = function.Call(entityDefinition, "DefinirVisibilidadesComandosWeb", entity);

    for comando in funcao.Vars["COMANDOS"]:
        widget.View.Commands["GRP_COMANDOS"].Items[comando.Key].Visible = comando.Value
