def InitializeRow(entity, row):
    cor = "warning" if entity.StatusAssinaturaDigital.Index == 1 else "success"
    row.GetCellByName("STATUSASSINATURADIGITAL").Text = "<span class='label label label-"+ cor + "'> "+ row.GetCellByName("STATUSASSINATURADIGITAL").Text +" </span>"
