def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import Entity

    form = row.Grid.ProviderWidget
    if form == None: return
    registro = form.Entity

    if row.EditCommand != None:
        row.EditCommand.Visible = registro.IsVisibleBTNEDITAR

    if row.DeleteCommand != None:
        row.DeleteCommand.Visible = registro.IsVisibleBTNEXCLUIR
