ScriptItemPortable.SourceCode:
def Truncar(campos, row):
    from System.Web import HttpUtility
    for campo in campos:
        texto = HttpUtility.HtmlDecode(row.GetCellByName(campo).Text)
        if len(texto) > 20:
            row.GetCellByName(campo).Tooltip = texto
            row.GetCellByName(campo).Text = texto[0:17] + '...'

def InitializeRow(entity, row):
    Truncar(['NOME'], row)
