def InitializeRow(entity, row):
    if entity == None: return

    statusConfirmado = entity.Fields["STATUS"].Value == 2
    statusCancelado = entity.Fields["STATUS"].Value == 3

    if entity.Fields["STATUS"].Value == 2:
        if row.EditCommand != None:
            row.EditCommand.Visible = not statusConfirmado 
        if row.DeleteCommand != None:
            row.DeleteCommand.Visible = not statusConfirmado
    elif entity.Fields["STATUS"].Value == 3:
        if row.EditCommand != None:
            row.EditCommand.Visible = not statusCancelado
        if row.DeleteCommand != None:
            row.DeleteCommand.Visible = not statusCancelado
