def InitializeRow(entity, row):
    if entity == None: return

    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")    

    from Benner.Corporativo.Definicoes.Suprimentos import CPRequisicaoCotacao

    if row.EditCommand != None:
        row.EditCommand.Visible = CPRequisicaoCotacao.PodeAlterarCotacao(entity.Fields["STATUS"].Value, entity.Fields["STATUSDIGITARODADA"].Value, entity.Fields["COTACAO"].Handle.Value)
    if row.DeleteCommand != None:
        row.DeleteCommand.Visible = CPRequisicaoCotacao.PodeExcluirCotacao(entity.Fields["STATUS"].Value, entity.Fields["STATUSDIGITARODADA"].Value, entity.Fields["COTACAO"].Handle.Value, entity.FilialHandle)

    comandoEntregasEspecifica = row.GetCustomCommand("CMD_ENTREGAESPECIFICAAGRUPADA")
    comandoPagamentos = row.GetCustomCommand("CMD_PAGAMENTOS")

    status = entity.Fields["STATUS"].Value;

    if comandoPagamentos != None: 
        comandoPagamentos.Enabled = status == 1

    if status == 2 or status ==  3 or status == 4 or status == 5 or entity.EnabledComandoEntregasEspecificas():
        comandoEntregasEspecifica.Enabled = False
    else:
        comandoEntregasEspecifica.Enabled = True

    if entity.EhCotacaoPortalFornecedores:
        row.EditCommand.Visible = False
        row.DeleteCommand.Visible = False
        comandoEntregasEspecifica.Enabled = False
        comandoPagamentos.Enabled = False

