ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if entity == None: return
    if row.GetCustomCommand("Estornar") != None: row.GetCustomCommand("Estornar").Enabled = entity.PodeEstornarDevolver()
    if row.GetCustomCommand("CMD_DEVOLVER") != None: row.GetCustomCommand("CMD_DEVOLVER").Enabled = entity.PodeEstornarDevolver() and not entity.ControlaLote()
    if row.GetCustomCommand("CMD_DEVOLVERLOTE") != None: row.GetCustomCommand("CMD_DEVOLVERLOTE").Enabled = entity.PodeEstornarDevolver() and entity.ControlaLote()
    if row.GetCustomCommand("InserirLotes") != None: row.GetCustomCommand("InserirLotes").Enabled = entity.ControlaLote()
    if row.GetCustomCommand("CMD_GERARREQUISICAO") != None: row.GetCustomCommand("CMD_GERARREQUISICAO").Enabled = entity.VisibilidadeComandoGerarRequisicao
