def Load(widget):
    import clr
    clr.AddReference("Benner.Erp.Web")
    from Benner.Erp.Web import AuthorizationHelper

    if widget.View.Commands["Save"] <> None: widget.View.Commands["Save"].Visible = widget.Entity.Handle.IsInvalid() and AuthorizationHelper.HasTask("MANTER_AGENDAMENTO_ENTREGAS_OC")
    if widget.View.Commands["CMD_EXCLUIR"] <> None: widget.View.Commands["CMD_EXCLUIR"].Visible = widget.Entity.Handle.IsValid() and AuthorizationHelper.HasTask("MANTER_AGENDAMENTO_ENTREGAS_OC")

def OnSaveExecute(command, entity):
    from Benner.Tecnologia.Common.Exceptions import CancelledOperationException
    if identity.Branch.ToString() == "0":
        raise CancelledOperationException("VocÃª precisa selecionar uma filial.")
    entity.TransitoryVars["COMANDO"] = "AGENDAR"
