def OnSaveExecute(command, entity):
    from Benner.Tecnologia.Common import TabItem
    from Benner.Tecnologia.Common import EntityAssociation
    from Benner.Tecnologia.Common import Handle

    if (not entity.Fields["TIPORESPONSAVEL"] is None):
        if (entity.Fields["TIPORESPONSAVEL"].Value == 2):
            entity.Fields["RESPONSAVEL"].Handle = Handle()
        elif (entity.Fields["TIPORESPONSAVEL"].Value == 1):
            entity.Fields["PAPEL"].Handle = Handle()

def OnTAREFAValueChanged(view, entity):
    from Benner.Tecnologia.Common import EntityBase
    from Benner.Tecnologia.Common import TabItem

    if (entity.Fields["TAREFA"].Handle.IsValid()):
        entity.Fields["EXIGIRAVALIACAO"] = entity.Fields["TAREFA"].Instance.Fields["EXIGIRAVALIACAO"]
        entity.Fields["EXIGIRARQUIVO"] = entity.Fields["TAREFA"].Instance.Fields["EXIGIRARQUIVO"]
