ScriptItemPortable.SourceCode:
def OnSTATUSValueChanged(view, entity):
    from Benner.Tecnologia.Common import FieldDefinition
    if entity.Fields["STATUS"].Value == 2:
        view.FieldDefinitions["OBSERVACOES"].Visible = True
        view.FieldDefinitions["OBSERVACOES"].Required = True
    else:
        view.FieldDefinitions["OBSERVACOES"].Visible = False
        view.FieldDefinitions["OBSERVACOES"].Required = False

def OnSaveExecute(command, entity):
    from Benner.Tecnologia.Common import TabItem
    from Benner.Tecnologia.Common import EntityAssociation
    from Benner.Tecnologia.Common import Handle

    if (entity.Fields["TIPORESPONSAVEL"] != None and entity.Fields["TIPORESPONSAVEL"].Value == 2):
        entity.Fields["RESPONSAVEL"].Handle = Handle()
    elif (entity.Fields["TIPORESPONSAVEL"] != None and entity.Fields["TIPORESPONSAVEL"].Value == 1):
        entity.Fields["PAPEL"].Handle = Handle()
