ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import BennerContext

    if entity == None: return;

    podeConfirmar = BennerContext.BusinessComponent.Call("Benner.Corporativo.Contabil.ImplantacaoSaldo.ImplantacaoSaldo, Benner.Corporativo.Contabil", "VerificarPodeConfirmarDocumento", entity.Handle.Value);
    podeBloquear = BennerContext.BusinessComponent.Call("Benner.Corporativo.Contabil.ImplantacaoSaldo.ImplantacaoSaldo, Benner.Corporativo.Contabil", "VerificarPodeBloquearDocumento", entity.Handle.Value);

    row.GetCustomCommand("CMD_CONFIRMAR").Enabled = bool(podeConfirmar) == True
    row.GetCustomCommand("CMD_BLOQUEAR").Enabled = bool(podeBloquear) == True

    if bool(entity.Fields["BLOQUEADO"]) == True:
        row.GetCellByName("BLOQUEADO").Align = "center"
        row.GetCellByName("BLOQUEADO").FontIcon = "fa fa-lock"
        row.GetCellByName("BLOQUEADO").TextColor = "red"
    else:
        row.GetCellByName("BLOQUEADO").Align = "center"
        row.GetCellByName("BLOQUEADO").FontIcon = "fa fa-unlock"
        row.GetCellByName("BLOQUEADO").TextColor = "green"
