def OnSaveExecute(command, entity):
    from Benner.Tecnologia.Common.Exceptions import CancelledOperationException
    if (entity["DESPESASMES"].GetInt32() == 0):
        raise CancelledOperationException("Cancelada a geração, informe um valor maior que zero para o total das despesas do mês!")
