ScriptItemPortable.SourceCode:
def Load(widget):
    provider = widget.ProviderWidget;
    if provider == None: return;
    registroPai = provider.Entity;
    if registroPai == None: return;
    bloqueado = registroPai.Fields["BLOQUEADO"];
    if bloqueado == None: return;
    widget.View.Commands["Edit"].Visible = bloqueado;
    widget.View.Commands["Delete"].Visible = bloqueado;
    widget.View.Commands["View"].Visible = True;
    widget.View.Commands["New"].Visible = bloqueado;
