ScriptItemPortable.SourceCode:
def Load(widget):
    registro = widget.Entity;
    if registro == None: return;
    bloqueado = registro.Fields["BLOQUEADO"];
    if bloqueado == None: return;
    widget.View.Commands["Edit"].Visible = bloqueado;
    widget.View.Commands["Delete"].Visible = bloqueado;
    widget.View.Commands["New"].Visible = False;
