def OnDeleteExecute(command, entity):
    from Benner.Tecnologia.Common import EntityDefinition
    entityDefinition = EntityDefinition.GetByName("CT_LANCAMENTOS");
    funcao = function.Call(entityDefinition, "VerificarLancamentoContraPartida", entity);
    if (bool(funcao.Vars["FOIEXCLUIDO"])):
        return;

def InitializeRow(entity, row):
    if (row.EditCommand != None):
        row.GetCustomCommand("CMD_EDITARWIZARD").Enabled = row.EditCommand.Visible;
        row.EditCommand.Visible = False;
    else:
        row.GetCustomCommand("CMD_EDITARWIZARD").Enabled = False;

def Load(widget):
    provider = widget.ProviderWidget;
    if provider == None: return;
    registroPai = provider.Entity;
    if registroPai == None: return;
    bloqueado = registroPai.Fields["BLOQUEADO"];
    if bloqueado == None: return;
    widget.View.Commands["CMD_NOVOWIZARD"].Visible = bloqueado;
    widget.View.Commands["Edit"].Visible = bloqueado;
    widget.View.Commands["Delete"].Visible = bloqueado;
    widget.View.Commands["View"].Visible = True;
    widget.View.Commands["New"].Visible = False;
