def OnNATUREZAValueChanged(view, entity):
    if entity == None: return;

    entity.TransitoryData["NATUREZA"] = str(entity.Fields["NATUREZA"]);

def Load(widget):
    if widget.Entity == None: return;

    widget.Entity.TransitoryData["NATUREZA"] = str(widget.Entity.Fields["NATUREZA"]);
