ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    registro = entity
    if registro == None: return

    if entity.Fields["VALOR"] != None: row.GetCellByName("VALOR").Text = str(str(str("{:,.2f}".format(entity.Fields["VALOR"])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
