def OnCMD_EXCLUIRExecute(command, entity):
from Benner.Tecnologia.Common import BreadcrumbEntityHandle, WesSiteMapNode command.RequestConfirmationBeforeExecute("Deseja realmente excluir as contabilizações ?") breadCrumbList = WesSiteMapNode.GetBreadcrumbEntityHandles()
item = breadCrumbList.Find(lambda found:found.EntityDefinitionName.Equals("TS_LANCAMENTOS")) command.TransitoryVars.Add("TS_LANCAMENTOSHANDLE", item.Handle)
